import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Search, 
  Filter, 
  UserPlus,
  Building,
  TrendingUp,
  TrendingDown,
  Plus,
  MoreVertical,
  Edit,
  Trash2
} from 'lucide-react';

const Teams = () => {
  const [selectedDepartment, setSelectedDepartment] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddMember, setShowAddMember] = useState(false);
  const [showAddTeam, setShowAddTeam] = useState(false);
  const [newTeamName, setNewTeamName] = useState('');
  const [openDropdown, setOpenDropdown] = useState(null);

  // Mock data
  const [departments, setDepartments] = useState([
    { id: 'design', name: 'Design', memberCount: 6 },
    { id: 'engineering', name: 'Engineering', memberCount: 12 },
    { id: 'product', name: 'Product', memberCount: 4 },
    { id: 'marketing', name: 'Marketing', memberCount: 5 },
    { id: 'sales', name: 'Sales', memberCount: 8 }
  ]);

  const addTeam = () => {
    if (newTeamName.trim()) {
      const newTeam = {
        id: newTeamName.toLowerCase().replace(/\s+/g, '-'),
        name: newTeamName,
        memberCount: 0
      };
      setDepartments([...departments, newTeam]);
      setNewTeamName('');
      setShowAddTeam(false);
    }
  };

  const deleteTeam = (teamId) => {
    if (window.confirm('Are you sure you want to delete this team? This action cannot be undone.')) {
      setDepartments(departments.filter(dept => dept.id !== teamId));
      if (selectedDepartment === teamId) {
        setSelectedDepartment('all');
      }
    }
  };

  const deleteMember = (memberId) => {
    if (window.confirm('Are you sure you want to remove this team member? This action cannot be undone.')) {
      // In a real app, this would update the backend
      // For now, we'll just show a success message
      alert('Team member removed successfully!');
    }
  };

  const handleDropdownAction = (action, itemId, itemType) => {
    setOpenDropdown(null);

    switch (action) {
      case 'remove':
        if (itemType === 'team') {
          deleteTeam(itemId);
        } else {
          deleteMember(itemId);
        }
        break;
      case 'edit':
        // TODO: Implement edit functionality
        alert('Edit functionality coming soon!');
        break;
      case 'add':
        if (itemType === 'team') {
          setShowAddTeam(true);
        } else {
          setShowAddMember(true);
        }
        break;
      default:
        break;
    }
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (openDropdown && !event.target.closest('.dropdown-container')) {
        setOpenDropdown(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [openDropdown]);

  const teamMembers = [
    {
      id: 1,
      name: 'Sarah Chen',
      email: 'sarah@company.com',
      role: 'Senior Designer',
      department: 'Design',
      signals: 4.8,
      lastOneOne: '2024-01-15',
      status: 'green',
      avatar: 'SC'
    },
    {
      id: 2,
      name: 'Mike Johnson',
      email: 'mike@company.com',
      role: 'Frontend Engineer',
      department: 'Engineering',
      signals: 4.6,
      lastOneOne: '2024-01-12',
      status: 'green',
      avatar: 'MJ'
    },
    {
      id: 3,
      name: 'Emma Davis',
      email: 'emma@company.com',
      role: 'Product Manager',
      department: 'Product',
      signals: 4.5,
      lastOneOne: '2024-01-10',
      status: 'yellow',
      avatar: 'ED'
    },
    {
      id: 4,
      name: 'Alex Kim',
      email: 'alex@company.com',
      role: 'Marketing Specialist',
      department: 'Marketing',
      signals: 4.4,
      lastOneOne: '2024-01-08',
      status: 'green',
      avatar: 'AK'
    },
    {
      id: 5,
      name: 'David Wilson',
      email: 'david@company.com',
      role: 'Sales Manager',
      department: 'Sales',
      signals: 4.3,
      lastOneOne: '2024-01-05',
      status: 'red',
      avatar: 'DW'
    },
    {
      id: 6,
      name: 'Lisa Brown',
      email: 'lisa@company.com',
      role: 'UX Designer',
      department: 'Design',
      signals: 3.8,
      lastOneOne: '2024-01-03',
      status: 'red',
      avatar: 'LB'
    },
    {
      id: 7,
      name: 'John Smith',
      email: 'john@company.com',
      role: 'Backend Engineer',
      department: 'Engineering',
      signals: 4.2,
      lastOneOne: '2024-01-14',
      status: 'green',
      avatar: 'JS'
    },
    {
      id: 8,
      name: 'Maria Garcia',
      email: 'maria@company.com',
      role: 'Product Designer',
      department: 'Product',
      signals: 4.7,
      lastOneOne: '2024-01-11',
      status: 'green',
      avatar: 'MG'
    }
  ];

  const filteredMembers = teamMembers.filter(member => {
    const matchesDepartment = selectedDepartment === 'all' || member.department.toLowerCase() === selectedDepartment;
    const matchesSearch = member.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         member.email.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesDepartment && matchesSearch;
  });

  const getStatusColor = (status) => {
    switch (status) {
      case 'green': return 'text-green-400';
      case 'yellow': return 'text-yellow-400';
      case 'red': return 'text-red-400';
      default: return 'text-white/60';
    }
  };

  const getSignalGradient = (signals) => {
    if (signals >= 4.5) return 'from-green-400 to-emerald-400';
    if (signals >= 4.0) return 'from-yellow-400 to-orange-400';
    return 'from-red-400 to-pink-400';
  };

  const getSignalGlow = (signals) => {
    if (signals >= 4.5) return 'shadow-green-400/50';
    if (signals >= 4.0) return 'shadow-yellow-400/50';
    return 'shadow-red-400/50';
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-white">Teams</h1>
          <p className="text-white/70 mt-1">Manage your team members and departments</p>
        </div>
        <div className="flex space-x-3">
          <button
            onClick={() => setShowAddTeam(true)}
            className="glass-button bg-mint text-gray-900 hover:bg-mint-dark"
          >
            <Plus className="w-5 h-5 mr-2" />
            Add Team
          </button>
          <button
            onClick={() => setShowAddMember(true)}
            className="glass-button bg-mint text-gray-900 hover:bg-mint-dark"
          >
            <UserPlus className="w-5 h-5 mr-2" />
            Add Member
          </button>
        </div>
      </div>

      {/* Filters */}
      <div className="glass-card p-6">
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60 w-5 h-5" />
              <input
                type="text"
                placeholder="Search members..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="glass-input w-full pl-10"
              />
            </div>
          </div>
          <div className="flex gap-4">
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
              className="glass-input"
            >
              <option value="all">All Departments</option>
              {departments.map(dept => (
                <option key={dept.id} value={dept.id}>{dept.name}</option>
              ))}
            </select>
            <button className="glass-button">
              <Filter className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Department Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
        {departments.map(dept => (
          <div 
            key={dept.id} 
            className={`glass-card p-4 text-center cursor-pointer transition-all duration-200 hover:bg-white/10 ${
              selectedDepartment === dept.id ? 'ring-2 ring-mint bg-mint/10' : ''
            }`}
            onClick={() => setSelectedDepartment(dept.id)}
          >
            <div className="relative">
              <div className="w-12 h-12 bg-mint/20 rounded-lg flex items-center justify-center mx-auto mb-3">
                <Building className="w-6 h-6 text-mint" />
              </div>
              <div className="absolute -top-2 -right-2">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    setOpenDropdown(openDropdown === `team-${dept.id}` ? null : `team-${dept.id}`);
                  }}
                  className="w-6 h-6 bg-white/10 hover:bg-white/20 rounded-full flex items-center justify-center text-white text-xs transition-colors backdrop-blur-sm"
                  title="More options"
                >
                  <MoreVertical className="w-3 h-3" />
                </button>

                {openDropdown === `team-${dept.id}` && (
                  <div className="absolute right-0 top-8 w-32 glass-card p-2 z-50 dropdown-container border border-white/20 shadow-2xl">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDropdownAction('edit', dept.id, 'team');
                      }}
                      className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-white hover:bg-white/10 rounded transition-colors"
                    >
                      <Edit className="w-4 h-4" />
                      <span>Edit</span>
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDropdownAction('remove', dept.id, 'team');
                      }}
                      className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-red-400 hover:bg-red-500/10 rounded transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                      <span>Remove</span>
                    </button>
                  </div>
                )}
              </div>
            </div>
            <h3 className="text-white font-semibold">{dept.name}</h3>
            <p className="text-white/60 text-sm">{dept.memberCount} members</p>
          </div>
        ))}
      </div>

      {/* Team Members Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredMembers.map(member => (
          <div key={member.id} className="glass-card p-6 hover:bg-white/25 transition-all duration-200">
            <div className="flex items-start justify-between mb-4">
              <Link to={`/app/teams/${member.id}`} className="flex-1">
                <div className="flex items-center space-x-3">
                  <div className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-semibold bg-gradient-to-r ${getSignalGradient(member.signals)}`}>
                    {member.avatar}
                  </div>
                  <div>
                    <h3 className="text-white font-semibold">{member.name}</h3>
                    <p className="text-white/60 text-sm">{member.role}</p>
                  </div>
                </div>
              </Link>
              <div className="flex items-center space-x-2">
                <div className="text-right">
                  <div className={`inline-flex items-center space-x-2 px-3 py-1 rounded-full bg-gradient-to-r ${getSignalGradient(member.signals)} ${getSignalGlow(member.signals)}`}>
                    <div className="w-2 h-2 rounded-full bg-white/80"></div>
                    <span className="text-white font-semibold text-sm">{member.signals}</span>
                  </div>
                </div>
                <div className="relative">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      setOpenDropdown(openDropdown === `member-${member.id}` ? null : `member-${member.id}`);
                    }}
                    className="w-8 h-8 bg-white/10 hover:bg-white/20 rounded-full flex items-center justify-center text-white transition-colors backdrop-blur-sm"
                    title="More options"
                  >
                    <MoreVertical className="w-4 h-4" />
                  </button>

                  {openDropdown === `member-${member.id}` && (
                    <div className="absolute right-0 top-10 w-32 glass-card p-2 z-10 dropdown-container">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDropdownAction('edit', member.id, 'member');
                        }}
                        className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-white hover:bg-white/10 rounded transition-colors"
                      >
                        <Edit className="w-4 h-4" />
                        <span>Edit</span>
                      </button>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDropdownAction('remove', member.id, 'member');
                        }}
                        className="w-full flex items-center space-x-2 px-3 py-2 text-sm text-red-400 hover:bg-red-500/10 rounded transition-colors"
                      >
                        <Trash2 className="w-4 h-4" />
                        <span>Remove</span>
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>

              <div className="space-y-2">
                <div className="flex items-center justify-between text-sm">
                  <span className="text-white/60">Department</span>
                  <span className="text-white">{member.department}</span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-white/60">Last 1:1</span>
                  <span className="text-white">{member.lastOneOne}</span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-white/60">Status</span>
                  <span className={`font-medium ${getStatusColor(member.status)}`}>
                    {member.status.charAt(0).toUpperCase() + member.status.slice(1)}
                  </span>
                </div>
              </div>

              <div className="mt-4 pt-4 border-t border-white/10">
                <div className="flex items-center justify-between text-sm">
                  <span className="text-white/60">Signal trend</span>
                  <div className="flex items-center">
                    {member.signals > 4.5 ? (
                      <TrendingUp className="w-4 h-4 text-green-400" />
                    ) : (
                      <TrendingDown className="w-4 h-4 text-red-400" />
                    )}
                  </div>
                </div>
              </div>
            </div>
        ))}
      </div>

      <>
        {/* Add Member Modal */}
        {showAddMember && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
            <div className="glass-card p-8 w-full max-w-md mx-4">
              <h2 className="text-2xl font-bold text-white mb-6">Add Team Member</h2>

              <form className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-white mb-2">Full Name</label>
                  <input type="text" className="glass-input w-full" placeholder="Enter full name" />
                </div>

                <div>
                  <label className="block text-sm font-medium text-white mb-2">Email</label>
                  <input type="email" className="glass-input w-full" placeholder="Enter email" />
                </div>

                <div>
                  <label className="block text-sm font-medium text-white mb-2">Role</label>
                  <input type="text" className="glass-input w-full" placeholder="Enter role" />
                </div>

                <div>
                  <label className="block text-sm font-medium text-white mb-2">Department</label>
                  <select className="glass-input w-full">
                    <option value="">Select department</option>
                    {departments.map(dept => (
                      <option key={dept.id} value={dept.id}>{dept.name}</option>
                    ))}
                  </select>
                </div>

                <div className="flex space-x-4 pt-4">
                  <button
                    type="button"
                    onClick={() => setShowAddMember(false)}
                    className="flex-1 glass-button"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="flex-1 glass-button bg-mint text-gray-900 hover:bg-mint-dark"
                  >
                    Add Member
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Add Team Modal */}
        {showAddTeam && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
            <div className="glass-card p-8 w-full max-w-md mx-4">
              <h2 className="text-2xl font-bold text-white mb-6">Add New Team</h2>

              <form className="space-y-4" onSubmit={(e) => { e.preventDefault(); addTeam(); }}>
                <div>
                  <label className="block text-sm font-medium text-white mb-2">Team Name</label>
                  <input 
                    type="text" 
                    className="glass-input w-full" 
                    placeholder="Enter team name"
                    value={newTeamName}
                    onChange={(e) => setNewTeamName(e.target.value)}
                    autoFocus
                  />
                </div>

                <div className="flex space-x-4 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowAddTeam(false);
                      setNewTeamName('');
                    }}
                    className="flex-1 glass-button"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="flex-1 glass-button bg-mint text-gray-900 hover:bg-mint-dark"
                  >
                    Add Team
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </>
    </div>
  );
};

export default Teams;
