import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  FileText, 
  Upload, 
  Share2
} from 'lucide-react';

const Dock = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [isVisible, setIsVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [scrollDirection, setScrollDirection] = useState('up');

  const dockItems = [
    { 
      icon: <Plus className="w-5 h-5" />, 
      label: 'Add Team Member', 
      description: 'Add new team member to your organization',
      onClick: () => window.location.href = '/app/teams',
      color: 'from-blue-400 to-cyan-400'
    },
    { 
      icon: <FileText className="w-5 h-5" />, 
      label: 'Create Survey', 
      description: 'Create a new team survey',
      onClick: () => window.location.href = '/app/surveys',
      color: 'from-green-400 to-emerald-400'
    },
    { 
      icon: <Upload className="w-5 h-5" />, 
      label: 'Upload 1:1', 
      description: 'Upload 1:1 meeting notes for analysis',
      onClick: () => window.location.href = '/app/entry',
      color: 'from-purple-400 to-pink-400'
    },
    { 
      icon: <Share2 className="w-5 h-5" />, 
      label: 'Invite', 
      description: 'Invite team members to SignalOS',
      onClick: () => {
        if (navigator.share) {
          navigator.share({
            title: 'Join SignalOS',
            text: 'Join our team performance platform',
            url: window.location.href
          });
        } else {
          navigator.clipboard.writeText(window.location.href);
          alert('Invite link copied to clipboard!');
        }
      },
      color: 'from-orange-400 to-red-400'
    }
  ];

  useEffect(() => {
    let scrollTimeout;

    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // Determine scroll direction
      if (currentScrollY > lastScrollY) {
        setScrollDirection('down');
      } else {
        setScrollDirection('up');
      }

      setLastScrollY(currentScrollY);

      // Hide dock when scrolling down
      if (scrollDirection === 'down') {
        setIsVisible(false);
      } else {
        // Show dock when scrolling up
        setIsVisible(true);
      }

      // Clear existing timeout
      clearTimeout(scrollTimeout);

      // Show dock after scrolling stops (for scroll up case)
      if (scrollDirection === 'up') {
        scrollTimeout = setTimeout(() => {
          setIsVisible(true);
        }, 150);
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
      clearTimeout(scrollTimeout);
    };
  }, [lastScrollY, scrollDirection]);

  return (
    <div className={`fixed bottom-6 left-1/2 transform -translate-x-1/2 z-50 transition-all duration-300 ease-out ${
      isVisible ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'
    }`}>
      <div className="glass-card px-6 py-4 rounded-2xl border border-white/20 backdrop-blur-xl shadow-2xl">
        <div className="flex items-center space-x-4">
          {dockItems.map((item, index) => {
            const isHovered = hoveredIndex === index;

            return (
              <div
                key={index}
                className="relative flex flex-col items-center justify-center cursor-pointer transition-all duration-300 ease-out group"
                onMouseEnter={() => setHoveredIndex(index)}
                onMouseLeave={() => setHoveredIndex(null)}
                onClick={item.onClick}
              >
                {/* Tooltip */}
                {isHovered && (
                  <div className="absolute -top-16 left-1/2 transform -translate-x-1/2 z-10">
                    <div className="px-4 py-2 rounded-xl text-sm text-white whitespace-nowrap border border-white/20 shadow-xl backdrop-blur-2xl bg-black/80">
                      <div className="font-semibold">{item.label}</div>
                      <div className="text-white/70 text-xs mt-1">{item.description}</div>
                    </div>
                    {/* Tooltip arrow */}
                    <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-black/80"></div>
                  </div>
                )}

                {/* Dock Item */}
                <div 
                  className={`w-14 h-14 rounded-xl flex items-center justify-center transition-all duration-300 ${
                    isHovered 
                      ? 'scale-105 shadow-lg bg-gradient-to-r ' + item.color + ' shadow-lg' 
                      : 'bg-white/10 hover:bg-white/20'
                  }`}
                >
                  <div className={`transition-all duration-300 ${
                    isHovered ? 'text-white' : 'text-white/80 group-hover:text-white'
                  }`}>
                    {item.icon}
                  </div>
                </div>


              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Dock;
